classValues:
flex items-center space-x-2 p-2 border rounded cursor-pointer transition-colors ${selectedFormats.includes(format.id)
                      ? 'bg-indigo-50 border-indigo-500'
                      : 'bg-white border-gray-200 hover:border-gray-300'
                    } ${isDisabled ? 'opacity-50 pointer-events-none' : ''}

p-4 rounded-lg border transition-all
                ${uploadFile.status === 'error' ? 'bg-red-50 border-red-200' : 'bg-white border-gray-200'}
                ${uploadFile.status === 'success' ? 'bg-green-50 border-green-200' : ''}

flex flex-col p-3 border-2 rounded-lg cursor-pointer transition-all ${
                        settings.difficultyLevel === option.value
                          ? 'border-indigo-600 bg-indigo-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }

flex items-center gap-2 p-2 border rounded-lg cursor-pointer transition-all ${
                        settings.questionTypes?.includes(type.id)
                          ? 'border-indigo-600 bg-indigo-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }

p-3 rounded-lg border ${
              showAnswer && opt.isCorrect ? 'border-green-300 bg-green-50' : 'border-gray-200 bg-white'
            }
